local tbNpc = Npc:GetClass("changeItemtoDong_npc");local tbItem = {	{18,1,289,10,2000},--tdc2	{18,1,289,9,2500},--tdc3	{18,1,289,8,3000},--tdc4	{18,1,289,7,3000},--tdc5}function tbNpc:OnDialog_1()    local szMsg = "Tỉ Lệ: LB TDC2 = 2000 đồng, TDC3 = 2500 Đồng, LB TDC4 = 3000 đồng, TDC5 = 3000 Đồng";		szMsg = "Tỉ Lệ: LB TDC2 = 2000 đồng, TDC3 = 2500 Đồng, LB TDC4 = 3000 đồng, TDC5 = 3000 Đồng";	Dialog:OpenGift(szMsg, nil, {self.OnOpenTrungOk, self});end;function tbNpc:OnOpenTrungOk(tbItemObj)		local tbItemList	= {};	local nBac = 0;	for _, pItem in pairs(tbItemObj) do		for _, item in pairs(tbItem) do			if (pItem[1].nGenre == item[1] and pItem[1].nDetail == item[2] and pItem[1].nParticular == item[3] and pItem[1].nLevel == item[4]) then				nBac = nBac + item[5];				break;			end		end	end		--add bac	me.AddJbCoin(nBac,0);		for _, pItem in pairs(tbItemObj) do		for _, item in pairs(tbItem) do			if (pItem[1].nGenre == item[1] and pItem[1].nDetail == item[2] and pItem[1].nParticular == item[3] and pItem[1].nLevel == item[4]) then				me.DelItem(pItem[1]);				break;			end		end	end				end;