local tbNpc = Npc:GetClass("changestone2");function tbNpc:OnDialog_1()	local szMsg = "Ta sẽ giúp nhà ngươi Ghép <color=green>Tinh Thạch Ảnh Nguyệt<color>.";		szMsg = "Tỉ lệ 5 <color=gold>Tinh Thạch Phượng Hoàng<color> = 1 <color=green>Tinh Thạch Ảnh Nguyệt<color>, Chia hết cho 5 (cho thừa sẽ bị mất).";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 1331 and pItem[1].nLevel == 1) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end	if number < 5 then		me.Msg("Cho đủ 5 <color=gold>Tinh Thạch Phượng Hoàng<color> vào.");		Dialog:Say("Cho đủ 5 <color=gold>Tinh Thạch Phượng Hoàng<color> vào.");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 1331, 1);		if nCount1 == 0 then			Dialog:Say("Bạn phải đặt <color=gold>Tinh Thạch Phượng Hoàng<color> trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 1331, 1);	local nMG = math.floor(number / 5);	me.AddStackItem(18,1,1331,2,nil,nMG);end;