local tbNpc = Npc:GetClass("changestone3");function tbNpc:OnDialog_1()	local szMsg = "Ta sẽ giúp nhà ngươi Ghép <color=blue>Tinh Thạch Đoạn Hải<color>.";		szMsg = "Tỉ lệ 20 <color=gold>Tinh Thạch Ảnh Nguyệt<color> = 1 <color=blue>Tinh Thạch Đoạn Hải<color>, Chia hết cho 20(cho thừa sẽ bị mất).";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 1331 and pItem[1].nLevel == 2) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end	if number < 20 then		me.Msg("Cho đủ 20 <color=green>Tinh Thạch Ảnh Nguyệt<color> vào.");		Dialog:Say("Cho đủ 20 <color=green>Tinh Thạch Ảnh Nguyệt<color> vào.");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 1331, 2);		if nCount1 == 0 then			Dialog:Say("Phải đặt <color=green>Tinh Thạch Ảnh Nguyệt<color> trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 1331, 2);	local nMG = math.floor(number / 20);	me.AddStackItem(18,1,1331,3,nil,nMG);	end;