local tbNpc = Npc:GetClass("changestone4");function tbNpc:OnDialog_1()	local szMsg = "Ta sẽ giúp nhà ngươi Ghép <color=red>Tinh Thạch Thánh Hỏa<color>.";		szMsg = "Tỉ lệ 40 <color=blue>Tinh Thạch Đoạn Hải<color> = 1 <color=red>Tinh Thạch Thánh Hỏa<color>, Chia hết cho 40 (cho thừa sẽ bị mất).";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 1331 and pItem[1].nLevel == 3) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end		if number < 40 then		me.Msg("Cho đủ 40 <color=blue>Tinh Thạch Đoạn Hải<color> vào.");		Dialog:Say("Cho đủ 40 <color=blue>Tinh Thạch Đoạn Hải<color> vào.");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 1331, 3);		if nCount1 == 0 then			Dialog:Say("Bạn phải đặt<color=blue>Tinh Thạch Đoạn Hải<color> trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 1331, 3);	local nMG = math.floor(number / 40);	me.AddStackItem(18,1,1331,4,nil,nMG);	end;