local tbNpc = Npc:GetClass("doirmg1");function tbNpc:OnDialog_1()	local szMsg = "Tỉ lệ: 40 <color=green>Danh Bổ lệnh<color> = 1 <color=yellow> Vạn Đồng Thường<color>";		szMsg = "Tỉ lệ: 40 <color=green>Danh Bổ lệnh<color> = 1 <color=yellow> Vạn Đồng Thường<color>";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 190 and pItem[1].nLevel == 1) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end	if number < 40 then		me.Msg("Cho Danh Bổ lệnh vào, Tỉ lệ: 40 <color=green>Danh Bổ lệnh<color> = 1 <color=yellow> Vạn Đồng Thường<color>(cho thừa sẽ bị mất).");		Dialog:Say("Cho Danh Bổ lệnh vào, Tỉ lệ: 40 <color=green>Danh Bổ lệnh<color> = 1 <color=yellow> Vạn Đồng Thường<color> (cho thừa sẽ bị mất).");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 190, 1);		if nCount1 == 0 then			Dialog:Say("Bạn phải để <color=green>Danh Bổ lệnh<color> trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 190, 1);	local nMG = math.floor(number / 40);	me.AddStackItem(18,1,1194,1,nil,nMG);end;