local tbNpc = Npc:GetClass("doithiepbac");function tbNpc:OnDialog_1()	local szMsg = "Ta sẽ giúp nhà ngươi đổi <color=yellow>Tiền Đồng Thường<color>.";		szMsg = "Cho <color=green>Thiệp Bạc<color> vào. Tỉ lệ: 1 <color=green>Thiệp Bạc<color> = 1 <color=yellow>Tiền Đồng Thường<color>.";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 541 and pItem[1].nLevel == 2) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end	if number < 1 then		me.Msg("Cho <color=green>Thiệp Bạc<color> vào. Tỉ lệ: 1 <color=green>Thiệp Bạc<color> = 2 <color=yellow>Tiền Đồng Thường<color>.");		Dialog:Say("Cho <color=green>Thiệp Bạc<color> vào. Tỉ lệ: 1 <color=green>Thiệp Bạc<color> = 2 <color=yellow>Tiền Đồng Thường<color>.");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 541, 2);		if nCount1 == 0 then			Dialog:Say("Bạn phải để <color=green>Thiệp Bạc<color> trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 541, 2);	local nMG = math.floor(number / 0.5);	me.AddStackItem(18,1,1194,4,nil,nMG);end;