local tbNpc = Npc:GetClass("lbtdc");function tbNpc:OnDialog_1()	local szMsg = "Ta sẽ giúp nhà ngươi đổi <color=yellow>LB Triệu Hồi Boss TDC<color>";		szMsg = "Cho Danh Bổ lệnh vào, số Danh Bổ lệnh chia hết cho 20 (cho thừa sẽ bị mất).";	Dialog:OpenGift(szMsg, nil, {self.Change, self});end;function tbNpc:Change(tbItemObj)		local number = 0;	for _, pItem in pairs(tbItemObj) do		if (pItem[1].nGenre == 18 and pItem[1].nDetail == 1 and pItem[1].nParticular == 190 and pItem[1].nLevel == 1) then			--Kiem tra Danh Bo Lenh			number = pItem[1].nCount + number;				end	end	if number < 20 then		me.Msg("Cho Danh Bổ lệnh vào, số Danh Bổ lệnh chia hết cho 20 (cho thừa sẽ bị mất).");		Dialog:Say("Cho Danh Bổ lệnh vào, số Danh Bổ lệnh chia hết cho 20 (cho thừa sẽ bị mất).");		return 0;	else		-- check is Rương		local nCount1 = me.GetItemCountInBags(18, 1, 190, 1);		if nCount1 == 0 then			Dialog:Say("Bạn phải để Danh Bổ Lệnh trong rương");			return 0;		end	end	me.ConsumeItemInBags(number, 18, 1, 190, 1);	local nMG = math.floor(number / 20);	me.AddStackItem(18,1,3048,6,nil,nMG);end;